SELECT * FROM sql_query.programmer;
SELECT P.name,FLOOR(datediff(current_timestamp,dob)/365) as age, course FROM sql_query.programmer p, sql_query.studies st WHERE p.name=st.name AND course LIKE 'Dcs';
SELECT SUM(sold*scost)-dcost FROM sql_query.software WHERE dev_in LIKE 'C';
SELECT name FROM sql_query.programmer WHERE name LIKE '%A%A%';
SELECT name FROM sql_query.programmer WHERE LENGTH(name)=5;
SELECT name FROM sql_query.programmer WHERE FLOOR((current_date-doj)/365)>2 AND sex='F' AND (prof1='Cobol' OR prof2='Cobol');
SELECT MIN(LENGTH(name)) FROM sql_query.programmer;
-- SELECT name, sex , SUBSTR(dob,1,2)|| '/'||SUBSTR(dob,4,3)||'/'||SUBSTR(dob,8,2) dob,SUBSTR(doj,1,2)||'/'||SUBSTR(doj,4,3)||'/'||SUBSTR(doj,8,2) doj FROM sql_query.programmer;
SELECT name FROM sql_query.programmer WHERE LAST_DAY(dob) = dob;
SELECT salary FROM sql_query.programmer WHERE sex='M' AND (prof1!='Cobol' OR prof2!='Cobol');
SELECT name FROM sql_query.programmer WHERE date_format(dob,'%M%')= date_format(doj,'%M%');
SELECT sex,COUNT(name) FROM sql_query.programmer GROUP BY sex;
SELECT date_format(dob,'%Y') AS YEAR,COUNT(name) FROM sql_query.programmer GROUP BY date_format(dob,'%Y');
SELECT date_format(doj,'%Y') AS YEAR,COUNT(name) FROM sql_query.programmer GROUP BY date_format(doj,'%Y');
SELECT SUBSTR(dob,4,3),COUNT(name) FROM sql_query.programmer GROUP BY SUBSTR(dob,4,3);
SELECT SUBSTR(doj,4,3),COUNT(name) FROM sql_query.programmer GROUP BY SUBSTR(doj,4,3);
SELECT prof1, COUNT(prof1) FROM sql_query.programmer GROUP BY prof1;
SELECT prof2, COUNT(prof2) FROM sql_query.programmer GROUP BY prof2;
SELECT salary,COUNT(name) FROM sql_query.programmer GROUP BY salary;
SELECT name,sex FROM sql_query.programmer ORDER BY sex;
SELECT MAX(salary), MIN(salary), AVG(salary) FROM sql_query.programmer WHERE salary > 2000;
SELECT * FROM sql_query.programmer WHERE salary=(SELECT MAX(salary) FROM sql_query.programmer WHERE prof1 LIKE 'C' OR prof2 LIKE 'C');
SELECT * FROM sql_query.programmer WHERE salary=(SELECT MAX(salary) FROM sql_query.programmer WHERE (prof1 LIKE 'Cobol' OR prof2 LIKE 'Cobol')) AND sex LIKE 'F';
SELECT DISTINCT name, salary, prof1 FROM sql_query.programmer WHERE (salary,prof1) IN (SELECT MAX(salary),prof1 FROM sql_query.programmer GROUP BY prof1);
SELECT FLOOR(datediff(current_date,doj)/365)as Exp, name FROM sql_query.programmer where FLOOR(datediff(current_date,doj)/365)<1;
SELECT FLOOR(datediff(current_date,doj)/365)as Exp,name,prof1,prof2 FROM sql_query.programmer where FLOOR(datediff(current_date,doj)/365)>1;
SELECT PROF1 FROM PROGRAMMER GROUP BY PROF1 HAVING PROF1 NOT IN (SELECT PROF2 FROM PROGRAMMER) AND COUNT(PROF1)=1 UNION SELECT PROF2 FROM PROGRAMMER GROUP BY PROF2 HAVING PROF2 NOT IN (SELECT PROF1 FROM PROGRAMMER) AND COUNT(PROF2)=1;
SELECT FLOOR(datediff(current_date,DOB)/365) AGE, NAME, PROF1, PROF2 FROM PROGRAMMER WHERE FLOOR(datediff(current_date,DOB)/365) = (SELECT MIN(FLOOR(datediff(current_date,DOB)/365)) FROM PROGRAMMER WHERE PROF1 LIKE 'DBASE' OR PROF2 LIKE 'DBASE');
SELECT * FROM PROGRAMMER WHERE SEX = 'F' AND SALARY >3000 AND (PROF1 NOT IN('C','C++','ORACLE','DBASE') OR PROF2 NOT IN('C','C++','ORACLE','DBASE'));
SELECT NAME FROM PROGRAMMER WHERE SEX = 'F' AND SALARY > (SELECT(MAX(SALARY)) FROM PROGRAMMER WHERE SEX = 'M');
SELECT PROF1 FROM PROGRAMMER GROUP BY PROF1 HAVING PROF1 = (SELECT MAX(PROF1) FROM PROGRAMMER);
SELECT NAME FROM PROGRAMMER WHERE DOB=(SELECT (MAX(DOB)) FROM PROGRAMMER WHERE date_format(DOB,'%Y') LIKE '1965');
SELECT NAME FROM PROGRAMMER WHERE DOJ=(SELECT (MIN(DOJ)) FROM PROGRAMMER WHERE date_format(DOJ,'%Y') LIKE '1992');
SELECT DISTINCT date_format(DOB,'%Y') FROM PROGRAMMER WHERE date_format(DOB,'%Y') = (SELECT MAX(date_format(DOB,'%Y')) FROM PROGRAMMER);
SELECT DISTINCT date_format(DOJ,'%M') FROM PROGRAMMER WHERE date_format(DOJ,'%M') = (SELECT MAX(date_format(DOJ,'%M')) FROM PROGRAMMER);
SELECT MAX(prof1) as Most_proficient FROM PROGRAMMER;
SELECT NAME FROM PROGRAMMER WHERE SEX = 'M' AND SALARY < (SELECT(AVG(SALARY))FROM PROGRAMMER WHERE SEX = 'F');
SELECT NAME, SALARY FROM PROGRAMMER WHERE SALARY = any(SELECT SALARY FROM PROGRAMMER P GROUP by SALARY HAVING SALARY=P.SALARY AND COUNT(*)>1);
SELECT * FROM PROGRAMMER WHERE date_format(DOJ,'YY')<90;
SELECT NAME FROM PROGRAMMER WHERE NAME not in(SELECT NAME FROM SOFTWARE);
SELECT NAME FROM PROGRAMMER WHERE Prof2 = any(SELECT Prof2 FROM PROGRAMMER GROUP by Prof2 HAVING COUNT(*) >1);
SELECT STUDIES.splace,SUM(SOFTWARE.SOLD*SOFTWARE.SCOST) FROM SOFTWARE,STUDIES WHERE STUDIES.NAME=SOFTWARE.NAME GROUP by STUDIES.splace;
SELECT splace FROM SOFTWARE st,STUDIES s WHERE s.NAME=st.NAME GROUP by splace,DCOST HAVING max(DCOST)=(SELECT max(DCOST) FROM SOFTWARE);
SELECT Prof1 FROM PROGRAMMER WHERE Prof1 not in(SELECT DEV_IN FROM SOFTWARE) union SELECT Prof2 FROM PROGRAMMER WHERE Prof2 not in(SELECT DEV_IN FROM SOFTWARE);
SELECT P1.SALARY,s2.course FROM PROGRAMMER P1,SOFTWARE s1,STUDIES s2 WHERE P1.NAME=s1.NAME AND s1.NAME=s2.NAME AND SCOST=(SELECT max(SCOST) FROM SOFTWARE);
SELECT P.NAME,ceil(ccost/SALARY) FROM PROGRAMMER P,STUDIES s WHERE s.NAME=P.NAME;
SELECT x.title FROM SOFTWARE x, PROGRAMMER y WHERE (datediff(current_date, y.DOJ)/12) > 3 AND x.NAME=y.NAME;
SELECT avg(SALARY) FROM PROGRAMMER P,SOFTWARE s WHERE P .NAME=s.NAME AND SOLD*SCOST>50000;
SELECT COUNT(s.NAME) FROM SOFTWARE s,STUDIES st WHERE s.NAME=st.NAME GROUP by s.NAME,ccost HAVING min(ccost)=(SELECT min(ccost) FROM STUDIES);
SELECT COUNT(*) FROM PROGRAMMER P,SOFTWARE s WHERE s .NAME=P.NAME GROUP by DEV_IN HAVING min(DCOST)=(SELECT min(DCOST) FROM SOFTWARE);
SELECT COUNT(DEV_IN) FROM PROGRAMMER P,SOFTWARE s WHERE s.NAME=P.NAME AND SEX='f' AND SALARY>(SELECT max(SALARY) FROM PROGRAMMER P,SOFTWARE s WHERE s.NAME=P.NAME AND SEX='m');
SELECT COUNT(*) FROM SOFTWARE S,PROGRAMMER P where P.NAME=S.NAME GROUP BY DOJ HAVING MAX(DOJ)=(select MAX(DOJ) FROM STUDIES ST,PROGRAMMER P, SOFTWARE S WHERE P.NAME=S.NAME and ST.NAME=P.NAME AND (splace='bdps'));
